  `period` text DEFAULT NULL,
  `日期` text DEFAULT NULL,
  `开盘` double DEFAULT NULL,
  `收盘` double DEFAULT NULL,
  `最高` double DEFAULT NULL,
  `最低` double DEFAULT NULL,
  `成交量` bigint(20) DEFAULT NULL,
  `成交额` double DEFAULT NULL,
  `振幅` double DEFAULT NULL,
  `涨跌幅` double DEFAULT NULL,
  `涨跌额` double DEFAULT NULL,
  `换手率` double DEFAULT NULL,
  `ds` text DEFAULT NULL,
  KEY `ds` (`ds`(768))
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;




show tables;

-- unique
select symbol, count(*) as cnt, max(open) as max_open, max(roc) as max_roc from (
    select distinct `symbol`, `日期`, `开盘` as open, `涨跌幅` as roc from stock_zh_a_hist
)a 
group by symbol;

select symbol, count(*) as cnt, max(open) as max_open, max(roc) as max_roc from (
    select distinct `symbol`, `日期`, `开盘` as open, `涨跌幅` as roc from index_zh_a_hist
)a 
group by symbol;

-- join 
select stock.*, idx.symbol as idx_symbol, idx.open as idx_open, idx.roc as idx_roc from (
    select distinct `symbol`, `日期` as date, `开盘` as open, `涨跌幅` as roc from stock_zh_a_hist
) stock 
join
(
    select distinct `symbol`, `日期` as date, `开盘` as open, `涨跌幅` as roc from index_zh_a_hist
) idx
on stock.date = idx.date
;

-- compute beta, mysql doesnot have covariance function.
select symbol, variance(roc) as roc_var, variance(idx_roc) as idx_roc_var from ( 
    select stock.*, idx.symbol as idx_symbol, idx.open as idx_open, idx.roc as idx_roc  from (
        select distinct `symbol`, `日期` as date, `开盘` as open, `涨跌幅` as roc from stock_zh_a_hist
    ) stock 
    join
    (
        select distinct `symbol`, `日期` as date, `开盘` as open, `涨跌幅` as roc from index_zh_a_hist
    ) idx
    on stock.date = idx.date
) si
group by symbol



